#define ll long long 
#define pb push_back

class Solution {
public:
    ll func(vector<vector<pair<int,int>>>& brd, int n, int m, int k, vector<int>& row, vector<int>& col) {
        if (k == 3) return 0; // base case: selected 3 elements

        ll ans = -1e18; // set to a very low value

        for (int i = 0; i < n; i++) {
            if (row[i] == 1) continue; // skip if this row is already used

            for (auto it : brd[i]) {
                int val = it.first;
                int j = it.second;

                if (col[j] == 1) continue; // skip if this column is already used

                // Mark row and column as used
                row[i] = 1;
                col[j] = 1;

                // Recursively call the function for the next selection
                ans = max(ans, val + func(brd, n, m, k + 1, row, col));

                // Backtrack to try other possibilities
                row[i] = 0;
                col[j] = 0;
            }
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();

        vector<vector<pair<int, int>>> brd(n);

        for (int i = 0; i < n; i++) {
            vector<pair<int, int>> v;

            for (int j = 0; j < m; j++) {
                v.pb({board[i][j], j});
            }

            // Sort the elements in each row
            sort(v.begin(), v.end());

            // Store all elements for now
            for (int j = 0; j < 3; j++) {
                brd[i].pb(v[m - 1 - j]);
            }
        }

        vector<int> row(n, 0), col(m, 0);
        return func(brd, n, m, 0, row, col);
    }
};
