class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        vector<vector<pair<int, int>>> pre(n);

        for (int i = 0; i < n; i++) {
            set<pair<int, int>> s;
            for (int j = 0; j < m; j++) {
                s.insert({a[i][j], j});
                if (s.size() > 3) s.erase(s.begin());
            }

            for (auto &j : s) {
                pre[i].push_back(j);
            }
            reverse(pre[i].begin(), pre[i].end());
        }

        long long ans = -3e9;
        //bool ok = 0;
        for (int i = 0; i < n - 2; i++) {
            for (int j = i + 1; j < n - 1; j++) {
                for (int k = j + 1; k < n; k++) {

                    for (auto &[iv, i_] : pre[i]) {
                        for (auto &[jv, j_] : pre[j]) {
                            if (i_ == j_) continue;
                            for (auto &[kv, k_] : pre[k]) {
                                if (i_ == k_ || j_ == k_) continue;
                                //ok = 1;
                                ans = max(ans, 1ll * iv + jv + kv);
                            }
                        }
                    }

                }
            }
        }

        return ans;
    }
};
