#define fi first
#define se second
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        const int n = board.size();
        const int m = board.front().size();
        long long ans = -3e9;
        bool usedr[505]{};
        bool usedc[505]{};
        vector<pair<int,pair<int,int>>> d;

        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                d.push_back({board[i][j], {i, j}});
            }
        }

        sort(d.begin(), d.end());
        reverse(d.begin(), d.end());
        d.resize(min(3*(n+m), n*m));
        const int X = d.size();

        for(int i = 0; i < X; i++)
            for(int j = i+1; j < X; j++)
                for(int k = j+1; k < X; k++) {
                    if(d[i].se.fi != d[j].se.fi && d[i].se.fi != d[k].se.fi && d[j].se.fi != d[k].se.fi
                    && d[i].se.se != d[j].se.se && d[i].se.se != d[k].se.se && d[j].se.se != d[k].se.se) {
                        ans = max(ans, (long long)d[i].fi + d[j].fi + d[k].fi);
                    }
                }

        return ans;
    }
};
