class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        arr = []
        n, m = len(board), len(board[0])

        for i in range(n):
            for j in range(m):
                arr.append([board[i][j], i, j])

        arr.sort(reverse=True)
        arr = arr[:100]

        ans = -float(inf)

        n2 = len(arr)

        for i in range(n2):
            for j in range(i + 1, n2):
                for k in range(j + 1, n2):
                    p1, x1, y1 = arr[i]
                    p2, x2, y2 = arr[j]
                    p3, x3, y3 = arr[k]

                    stx = set([x1, x2, x3])
                    sty = set([y1, y2, y3])

                    # print(stx, sty)

                    if len(stx) == 3 and len(sty) == 3:
                        ans = max(ans, p1 + p2 + p3)

        return ans
