class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        // vector<vector<int>>b(100, vector<int>(100, 100));
        int n=b.size(), m=b[0].size();
        long long dp[n][m][m];

        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++)
                for(int k=0;k<m;k++)
                    dp[i][j][k]=-1e9;
        }
        for(int j=0;j<m;j++) {
            for(int k=j+1;k<m;k++) {

                int x=-1e9;
                for(int i=0;i<n;i++) {
                    for(int l=0;l<m;l++) {
                        if(l==j || l==k) continue;
                        x=max(x, b[i][l]);
                    }
                    dp[i][j][k]=x;
                }
            }
        }
        long long an=-3e9;

        for(int i=1;i<n;i++) {
            for(int j=0;j<m;j++) {
                for(int k=i+1;k<n;k++) {
                    for(int l=0;l<m;l++) {
                        if(j==l) continue;
                        // cout<<i<<" "<<k<<" "<<dp[i-1][j][l]+b[i][j]+b[k][l]<<endl;
                        an=max(an, dp[i-1][min(j,l)][max(j,l)]+b[i][j]+b[k][l]);
                    }
                }
            }
        }
        return an;


    }
};
