class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n = len(board)
        m = len(board[0])
        v = []
        for i in range(n):
            for j in range(m):
                v.append([board[i][j],i,j])
        v.sort(reverse=True)
        ans = -3*10**9
        #print(v)
        for i in range(n*m):
            sm = v[i][0]
            r = {v[i][1]}
            c = {v[i][2]}
            for j in range(n+m):
                if i==j: continue
                x,f,l = v[j]
                if f in r or l in c:
                    continue
                sm += x
                r.add(f)
                c.add(l)
                for k in range(n*m):
                    if k==i or k==j:
                        continue
                    y,a,b = v[k]
                    if a in r or b in c:
                        continue
                    ans = max(ans, sm+y)
                    break
                r.remove(f)
                c.remove(l)
                sm -=x
        return ans
