typedef long long ll;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long int i,j,k;
        ll n = board.size(), m = board[0].size();
        vector<vector<pair<ll,ll>>> a(n);
        ll curr = 1;
        for(i=0;i<n;i++)
        {
            vector<pair<ll,ll>> v;
            for(j=0;j<m;j++)
            v.push_back({board[i][j],j});
            sort(v.begin(),v.end());
            if(m == 3)
            {
                a[i].push_back(v[0]);
                a[i].push_back(v[1]);
                a[i].push_back(v[2]);
            }
            else
            {
                if(v[m-4] == v[m-1])
                {
                    a[i].push_back({v[m-1].first, curr});
                    curr++;
                }
                else
                {
                    a[i].push_back({v[m-1].first, v[m-1].second});
                    if(v[m-4] == v[m-2])
                    {
                        a[i].push_back({v[m-2].first, curr});
                        curr++;
                    }
                    else
                    {
                        a[i].push_back({v[m-2].first, v[m-2].second});
                        if(v[m-4] == v[m-3])
                        {
                        a[i].push_back({v[m-3].first, -curr});
                        curr++;
                        }
                        else
                        {
                            a[i].push_back({v[m-3].first, v[m-3].second});
                        }
                    }
                }

            }
        }
        ll ans = -4e9;
        for(i=0;i<n;i++)
        {
            for(j=0;j<a[i].size();j++)
            cout<<a[i][j].first<<" ";
            cout<<endl;
        }
        for(i=0;i<n;i++)
        {
            for(j=0;j<a[i].size();j++)
            {
                for(ll ii=i+1;ii<n;ii++)
                {
                    for(ll jj=0;jj<a[ii].size();jj++)
                    {
                        for(ll i3=ii+1;i3<n;i3++)
                        {
                            for(ll j3=0;j3<a[i3].size();j3++)
                            {
                                // cout<<i<<" "<<j<<endl;
                                ll x = a[i][j].second;
                                ll y = a[ii][jj].second;
                                ll z = a[i3][j3].second;
                                if(x != y && y != z && x != z)
                                {
                                    ans = max(ans, a[i][j].first+a[ii][jj].first+a[i3][j3].first);
                                }
                            }
                        }
                    }
                }
            }
            cout<<endl;
        }
        return ans;
    }
};
