class Solution {
    public long maximumValueSum(int[][] board) {
       int n=board.length,m=board[0].length;
       int[][] a=new int[m][3];
        for(int i=0;i<m;i++){
          List<int[]>b=new ArrayList<>();
            for(int j=0;j<n;j++){
               b.add(new int[]{board[j][i],j}); 
            }
            Collections.sort(b,(c,d)->{
                if(c[0]<d[0])return 1;
                else return -1;
            });
            a[i][0]=b.get(0)[1];
            a[i][1]=b.get(1)[1];
            a[i][2]=b.get(2)[1];

        }
        for(int i=0;i<m;i++){
            for(int j=0;j<3;j++){
                System.out.print(a[i][j]+" ");
            }
            System.out.println("");
        }
        long maxi=-1000000000000L;
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int k=j+1;k<m;k++){
                   for(int p=0;p<3;p++){
                       for(int q=0;q<3;q++){
                           for(int r=0;r<3;r++){
                               int x=a[i][p],y=a[j][q],z=a[k][r];
                               if(x!=y&&y!=z&&x!=z){
                                   long t=(long)board[x][i]+board[y][j]+board[z][k];
                                   maxi=Math.max(maxi,t);
                               }
                           }
                       }
                   } 
                }
            }
        }
        return maxi;
    }
}
