class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        vector<pair<long long ,pair<int,int>>> v;
        int i,j,k;
        long long ans=-1e10;
        for(i=0;i<a.size();i++){
            for(j=0;j<a[0].size();j++){
                v.push_back({a[i][j],{i,j}});
            }
        }
        int pe=2*max(a.size(),a[0].size())+3,p,q,r,s,x,y;
        sort(v.begin(),v.end());
        for(i=v.size()-pe;i<v.size();i++){
            p=v[i].second.first;
            q=v[i].second.second;
            for(j=v.size()-pe;j<v.size();j++){
                r=v[j].second.first;
                s=v[j].second.second;
                if(r!=p&&s!=q){
                for(k=v.size()-pe;k<v.size();k++){
                       x=v[k].second.first;
                       y=v[k].second.second;
                       if(x!=r&&x!=p&&y!=s&&y!=q){
                           if(a[p][q]+0LL+a[r][s]+a[x][y]>ans)
                            ans=a[p][q]+0LL+a[r][s]+a[x][y];
                       }
                }
            }
        }
    }
    return ans;}
};
