#define ll long long 
#define pb push_back

class Solution {
public:
    ll func(vector<vector<pair<int,int>>>& nums, int n, int m, int k, vector<int>& row, vector<int>& col) {
        if (k == 3) return 0;
        ll ans = -1e15;
        for (int i = 0; i < n; i++) {
            if (row[i] == 1) continue;
            for (auto it : nums[i]) {
                int val = it.first;
                int j = it.second;
                if (col[j] == 1) continue;
                row[i] = 1;
                col[j] = 1;
                ans = max(ans, val + func(nums, n, m, k + 1, row, col));
                row[i] = 0;
                col[j] = 0;
            }
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int, int>>> nums(n);
        for (int i = 0; i < n; i++) {
            vector<pair<int, int>> v;

            for (int j = 0; j < m; j++) {
                v.pb({board[i][j], j});
            }
            sort(v.begin(), v.end());
            for (int j = 0; j < 3; j++) {
                nums[i].pb(v[m - 1 - j]);
            }
        }

        vector<int> row(n, 0), col(m, 0);
        return func(nums, n, m, 0, row, col);
    }
};
