class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<long long,long long>>> row(n);

        for(int i = 0; i < n; i++) {
            vector<pair<long long,long long>> m1;
            for(int j = 0; j < m; j++) {
                m1.push_back({board[i][j], j});
            }
            sort(m1.rbegin(), m1.rend()); 
            row[i].push_back(m1[0]);
            row[i].push_back(m1[1]);
            row[i].push_back(m1[2]);
        }

        long long ans = LLONG_MIN;

        for(int i = 0; i < n - 2; i++) {
            for(int m1 = 0; m1 < 3; m1++) {
                for(int j = i + 1; j < n - 1; j++) {
                    for(int m2 = 0; m2 < 3; m2++) {
                        if(row[j][m2].second != row[i][m1].second) {
                            for(int k = j + 1; k < n; k++) {
                                for(int m3 = 0; m3 < 3; m3++) {
                                    if(row[k][m3].second != row[j][m2].second && row[k][m3].second != row[i][m1].second) {
                                        long long temp = row[i][m1].first + row[j][m2].first + row[k][m3].first;
                                        ans = max(ans, temp);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
