class Solution {
    public long maximumValueSum(int[][] board) {
        int n = board.length, m = board[0].length;
        long dp1[][][] = new long[n][m][m];
        for(int i = n-1; i>=0;i--){
            for(int j = 0;j<m;j++){
                for(int k = 0; k<m;k++){
                    dp1[i][j][k] = Integer.MIN_VALUE;
                    if(i+1<n)dp1[i][j][k]=Math.max(dp1[i][j][k],dp1[i+1][j][k]);
                    for(int f = 0; f<m;f++){
                        if(f!=j&&f!=k)dp1[i][j][k] = Math.max(dp1[i][j][k], board[i][f]*1l);
                    }
                }
            }
        }
        long dp2[][]= new long[n][m];
        for(int i = n-2; i>=0;i--){
            for(int j = 0; j<m;j++){
                dp2[i][j] = Integer.MIN_VALUE;
                if(i+1<n-1)dp2[i][j] = dp2[i+1][j];
                for(int k = 0; k<m;k++){
                    if(k!=j){
                        dp2[i][j] = Math.max(dp2[i][j],board[i][k]*1l+dp1[i+1][j][k]);
                    }
                }
            }
        }
        long ans = Long.MIN_VALUE;
        for(int i = n-3;i>=0;i--){
            for(int j = 0; j<m;j++){
                ans = Math.max(ans, board[i][j]*1l + dp2[i+1][j]);
            }
        }
        return ans;
    }
}
