class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();

        vector<multiset<pair<long long,int>>> dp(n);


        for (int i=0;i<n;i++) {
            for (int j=0;j<m;j++) {
                dp[i].insert({board[i][j],j});
            }
        }

        long long res = LONG_MIN;
        set<int> ind;
        for (int i=0;i<n;i++) {

            auto it1 = dp[i].end();
            it1--;
            long long tmp = it1->first;
            ind.insert(it1->second);

            for (int j=0;j<n;j++) {
                if (j==i) continue;

                auto it2 = dp[j].end();
                it2--;
                if (ind.find(it2->second) == ind.end()) {
                    tmp += it2->first;
                    ind.insert(it2->second);
                } else {
                    it2--;
                    tmp += it2->first;
                    ind.insert(it2->second);
                }

                for (int k=0;k<n;k++) {
                    if (i==k || j==k) continue;
                    auto it3 = dp[k].end();
                    it3--;
                    if (ind.find(it3->second) == ind.end()) {
                        tmp += it3->first;
                    } else {
                        it3--;
                        if (ind.find(it3->second) == ind.end()) {
                            tmp += it3->first; 
                        } else {
                            it3--;
                            tmp += it3->first;
                        }
                    }
                    res = max(tmp,res);
                    tmp -= it3->first;
                }
                ind.erase(it2->second);
                tmp -= it2->first;
            }
            ind.erase(it1->second);
        }

        return res;
    }
};
