import java.util.Arrays;
class Solution {
long ans=Long.MIN_VALUE;
    public long maximumValueSum(int[][] board) {
        int[][] topIndices = new int[board.length][3];

        for (int i = 0; i < board.length; i++) {
            int[] row = board[i];

            int first = Integer.MIN_VALUE, second = Integer.MIN_VALUE, third = Integer.MIN_VALUE;
            int firstIndex = -1, secondIndex = -1, thirdIndex = -1;

            for (int j = 0; j < row.length; j++) {
                int value = row[j];

                if (value > first) {
                    third = second;
                    thirdIndex = secondIndex;
                    second = first;
                    secondIndex = firstIndex;
                    first = value;
                    firstIndex = j;
                } else if (value > second) {
                    third = second;
                    thirdIndex = secondIndex;
                    second = value;
                    secondIndex = j;
                } else if (value > third) {
                    third = value;
                    thirdIndex = j;
                }
            }

            topIndices[i][0] = firstIndex;
            topIndices[i][1] = secondIndex;
            topIndices[i][2] = thirdIndex;
        }
        // long ans=0;
          solve(0,board,new HashSet<>(),0,0,topIndices);

        return ans;
    }
private void solve(int i, int[][] board, Set<Integer> set, int cv, long cs, int[][] topIndices) {
    int n = board.length;
    if (i == n) {
        return;
    }
    for (int j = 0; j < 3; j++) {
        int col = topIndices[i][j];
        if (set.contains(col)) {
            continue;
        }
        long v = (long) board[i][col];
        if (cv == 2) {
            ans = Math.max(ans, v + cs);
        } else {
            set.add(col);
            solve(i + 1, board, set, cv + 1, cs + v, topIndices);
            set.remove(col);
        }
    }
    solve(i + 1, board, set, cv, cs, topIndices);
}

}
