class Solution {
public:
    void solve(int k, vector<int> vis, vector<int> vis1, vector<pair<int,pair<int,int>>>& v, int index, long long& ans, long long curr){
        if(k==0){
            ans=max(ans,curr);
            return;
        }
        int a=k;
        for(int i=index-1;i>=0;i--){
            if(!vis[v[i].second.first] && !vis1[v[i].second.second]){
                vis[v[i].second.first]=1;
                vis1[v[i].second.second]=1;
                //cout<<k<<' '<<v[i].first<<endl;
                solve(k-1,vis,vis1,v,i,ans,curr + (long long)v[i].first);
                vis[v[i].second.first]=0;
                vis1[v[i].second.second]=0;
                a--;
                if(a==0) break;
            }
        }

    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        vector<int> vis(n,0);
        vector<int> vis1(m,0);
        long long ans=LLONG_MIN;
        set<pair<int,pair<int,int>>> s;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                s.insert({board[i][j],{i,j}});
            }
        }
        vector<pair<int,pair<int,int>>> v(s.begin(),s.end());
        int ni=v.size();
        for(int i=1;i<=ni;i++){
            vis[v[ni-i].second.first]=1;
            vis1[v[ni-i].second.second]=1;
            //cout<<3<<' '<<v[ni-i].first<<endl;
            solve(2,vis,vis1,v,ni-i,ans,(long long )v[ni-i].first);
            vis[v[ni-i].second.first]=0;
            vis1[v[ni-i].second.second]=0;
        }
        return ans;
    }
};
