vector<vector<pair<int, int>>> findTopThreeIndicesInRows(const vector<vector<int>>& board) {
    int m = board.size();
    vector<vector<pair<int, int>>> topThree(m, vector<pair<int, int>>(3, {INT_MIN, -1})); // 存储前三大值及其索引

    for (int i = 0; i < m; ++i) {
        vector<pair<int, int>> rowWithIndex; // 存储值和索引
        rowWithIndex.reserve(board[i].size());
        for (int j = 0; j < board[i].size(); ++j) {
            rowWithIndex.push_back({board[i][j], j});
        }
        // 按照值的大小降序排序
        sort(rowWithIndex.begin(), rowWithIndex.end(), greater<pair<int, int>>());

        // 记录前三大值及其索引
        for (int j = 0; j < 3; ++j) {
            topThree[i][j] = rowWithIndex[j];
        }
    }

    return topThree;
}
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board.front().size();
        auto maxTopThree = findTopThreeIndicesInRows(board);
        long long ans = std::numeric_limits<long long>::min();
        // 枚举两个车在哪一行
        for (int x1 = 0; x1 < n; x1 += 1) {
            for (auto i1{0}; i1 < 3; i1 += 1) {
                int y1 = maxTopThree[x1][i1].second;
                long long v1 = maxTopThree[x1][i1].first;
                for (int x2 = 0; x2 < n; x2 += 1) {
                    if (x1 == x2) continue;
                    for (auto i2{0}; i2 < 3; i2 += 1) {
                        int y2 = maxTopThree[x2][i2].second;
                        long long v2 = maxTopThree[x2][i2].first;
                        if (y1 == y2) {
                            continue;
                        }
                        // 确认第三个
                        for (int x3 = 0; x3 < n; x3 += 1) {
                            if (x1 == x3 || x2 == x3) continue;
                            for (auto i3{0}; i3 < 3; i3 += 1) {
                                int y3 = maxTopThree[x3][i3].second;
                                long long v3 = maxTopThree[x3][i3].first;
                                if (y1 == y3 || y2 == y3) {
                                    continue;
                                }
                                ans = std::max(ans, v1 + v2 + v3);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
