class Solution {
public:
    using ll = long long;
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        vector<vector<pair<int, int>>> top9(m);
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                top9[i].push_back({board[i][j], j});
            }
            sort(top9[i].begin(), top9[i].end(), greater<pair<int, int>>());
            if (top9[i].size() > 6) top9[i].resize(6);
        }

        ll max_sum = LLONG_MIN;

        for (int i = 0; i < m; ++i) {
            for (int j = i + 1; j < m; ++j) {
                for (int k = j + 1; k < m; ++k) {
                    for (auto& vi : top9[i]) {
                        for (auto& vj : top9[j]) {
                            if (vj.second == vi.second) continue;
                            for (auto& vk : top9[k]) {
                                if (vk.second == vi.second || vk.second == vj.second) continue;
                                ll sum = (ll)vi.first + vj.first + vk.first;
                                max_sum = max(max_sum, sum);
                            }
                        }
                    }
                }
            }
        }

        return max_sum;
    }
};
