using ll = long long;

class Solution {
public:
    bool eq(array<int,3> &a, array<int,3> &b) {
        return a[1] == b[1] || a[2] == b[2];
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<array<int,3>> q;
        vector<int> idx(n);
        for (int i = 0; i < m; i++) {
            iota(idx.begin(),idx.end(),0);
            sort(idx.begin(),idx.end(),[&](int j1, int j2) { return board[i][j1] < board[i][j2]; });
            for (int j = n-3; j < n; j++) q.push_back({board[i][idx[j]],i,idx[j]});
        }
        sort(q.begin(),q.end());
        ll ans = ll(-1e9)*3;
        for (int i = q.size()-1; i >= 0; i--) {
            auto &qi = q[i];
            for (int j = i-1; j >= 0; j--) {
                auto &qj = q[j];
                if (eq(qj, qi)) continue;
                for (int k = j-1; k >= 0; k--) {
                    auto &qk = q[k];
                    if (eq(qk,qj) || eq(qk,qi)) continue;
                    ll s = ll(qi[0]) + qj[0] + qk[0];
                    if (s >= ans) {
                        ans = s;
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
