#define long long
class Solution {

public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        long long ans = -LLONG_MAX;
        pair<long long,long long> mx[n+10][3];
        for(int i = 1;i<=n;i++){
            vector<pair<int,int>> lol;
            for(int j= 0;j<m;j++){
                lol.push_back(make_pair(board[i-1][j],j));
            }
            sort(lol.begin(),lol.end());
            for(int j = 0;j<3;j++){mx[i][j] = lol.back();
            lol.pop_back();}
        }
        for(int i = 1;i<=n;i++){
            for(int j = 1;j<=n;j++){
                for(int k = 1;k<=n;k++){
                    if(!(i != k && i != j && j != k)) continue;
                    for(int i2 = 0;i2<3;i2++)
                    {pair<long long int, long long int> solu = mx[i][i2];
                    for(int j2 = 0;j2<3;j2++){
                        for(int k2 = 0;k2<3;k2++){
                            pair<int,int> idx1 = mx[j][j2];
                            pair<int,int> idx2 = mx[k][k2];
                            if(solu.second != idx1.second && solu.second != idx2.second && idx1.second != idx2.second){
                                ans = max(ans, solu.first + idx1.first + idx2.first);
                            }
                        }
                    }}
                }
            }
        }
        return ans;
    }
};
