static pair<int, int> F[100][100];
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int const N = board.size(), M = board[0].size();
        for (int i = 0; i < N; ++i)
            for (int j = 0; j < M; ++j)
                F[i][j] = { board[i][j], j };
        for (int i = 0; i < N; ++i)
            sort(&F[i][0], &F[i][0] + M, greater{});
        int const Y = min(M, 3);
        long long result = 0LL + board[0][0] + board[1][1] + board[2][2];
        vector<int> A(3), B(3), Z(3), V(3);
        for(A[0] = 0; A[0] < N; ++A[0])
            for (B[0] = 0; B[0] < Y; ++B[0])
            {
                Z[0] = F[A[0]][B[0]].second;
                V[0] = F[A[0]][B[0]].first;
                for (A[1] = A[0] + 1; A[1] < N; ++A[1])
                    for (B[1] = 0; B[1] < Y; ++B[1])
                    {
                        Z[1] = F[A[1]][B[1]].second;
                        if (Z[1] == Z[0])
                            continue;
                        V[1] = F[A[1]][B[1]].first;
                        for (A[2] = A[1] + 1; A[2] < N; ++A[2])
                            for (B[2] = 0; B[2] < Y; ++B[2])
                            {
                                Z[2] = F[A[2]][B[2]].second;
                                if (Z[2] == Z[0] || Z[2] == Z[1])
                                    continue;
                                V[2] = F[A[2]][B[2]].first;
                                result = max(result, 0LL + V[0] + V[1] + V[2]);
                            }
                    }
            }
        return result;
    }
};
