#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size();
        ll m = board[0].size();
        vector<vector<pair<ll,ll>>>pqs(n);
        for (ll i = 0; i<n; i++){
            for (ll j = 0; j<m; j++){
                pqs[i].push_back({board[i][j],j});
            }
        }
        for (ll i = 0; i<n; i++){
            sort(pqs[i].begin(),pqs[i].end());
            reverse(pqs[i].begin(),pqs[i].end());
        }
        ll fans = -1e18;
        for (ll i = 0; i<n; i++){
            for (ll j = 0; j<n; j++){
                for (ll k = 0; k<n; k++){
                    if(i != j && i != k && j != k){
                        ll ans = 0;
                        ans += pqs[i][0].first*1ll;
                        int fr = pqs[i][0].second;
                        int a = 0;
                        while (a<m && pqs[j][a].second == fr){
                            a++;
                        }
                        if(a<m){
                            int sr = pqs[j][a].second;
                            ans += pqs[j][a].first*1ll;
                            int b = 0;
                            while (b<m && (pqs[k][b].second == fr || pqs[k][b].second == sr)){
                                b++;
                            }
                            if(b<m){
                                ans += pqs[k][b].first*1ll;
                                fans = max(ans,fans);
                            }
                        }
                    }
                }
            }
        }
        return fans;
    }
};
