class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
         int m = board.size();
        int n = board[0].size();
        vector<vector<pair<int, int>>> row(m), col(n);
        for (int i = 0; i < m; i++) {
            vector<pair<int, int>> t;
            for (int j = 0; j < n; j++) {
                t.push_back({board[i][j], j});
            }
            sort(t.rbegin(), t.rend()); 
            row[i] = vector<pair<int, int>>(t.begin(), t.begin() + min(3, n));
        }

        for (int j = 0; j < n; j++) {
            vector<pair<int, int>> t;
            for (int i = 0; i < m; i++) {
                t.push_back({board[i][j], i});
            }
            sort(t.rbegin(), t.rend()); 
            col[j] = vector<pair<int, int>>(t.begin(), t.begin() + min(3, m));
        }

        long long maxi = LLONG_MIN;
        for (int i1 = 0; i1 < m; i1++) {
            for (int j1 = 0; j1 < row[i1].size(); j1++) {
                int col1 = row[i1][j1].second;
                long long val1 = row[i1][j1].first;

                for (int i2 = i1 + 1; i2 < m; i2++) {
                    for (int j2 = 0; j2 < row[i2].size(); j2++) {
                        int col2 = row[i2][j2].second;
                        long long val2 = row[i2][j2].first;

                        if (col2 == col1) continue;

                        for (int i3 = i2 + 1; i3 < m; i3++) {
                            for (int j3 = 0; j3 < row[i3].size(); j3++) {
                                int col3 = row[i3][j3].second;
                                long long val3 = row[i3][j3].first;

                                if (col3 == col1 || col3 == col2) 
                                    continue;

                                maxi = max(maxi, val1 + val2 + val3);
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }
};
