class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n=board[0].size() ;
        vector<vector<long long>> scors(m*n, vector<long long>(3,0)) ;
        for(int i=0; i<m; i++){
            for(int j=0; j<n; j++){
                scors[i*n+j][0] = -board[i][j] ;
                scors[i*n+j][1] = i ;
                scors[i*n+j][2] = j ;
            }
        }
        sort(scors.begin(), scors.end()) ;
        long long r = -9999999999 ;
        long long t = 0 ;

        vector<bool> col(101, false) ;
        vector<bool> row(101, false) ;
        for(int i=0; i<scors.size()-2; i++){
            if(-scors[i][0]-scors[i+1][0]-scors[i+2][0]<r) break ;
            t -= scors[i][0] ;
            row[scors[i][1]] = true ;
            col[scors[i][2]] = true ;

            for(int j=i+1; j<scors.size()-1; j++){
                if(t-scors[j][0]-scors[j+1][0]<r) break ;

                if(row[scors[j][1]] || col[scors[j][2]]) continue ;
                t -= scors[j][0] ;
                row[scors[j][1]] = true ;
                col[scors[j][2]] = true ;               

                for(int k=j+1; k<scors.size(); k++){
                    if(row[scors[k][1]] || col[scors[k][2]]) continue ;

                    t -= scors[k][0] ;
                    r = max(r, t) ;
                    t += scors[k][0] ;
                    break ; 
                }

                t += scors[j][0] ;
                row[scors[j][1]] = false ;
                col[scors[j][2]] = false ;

            }

            t += scors[i][0] ;
            row[scors[i][1]] = false ;
            col[scors[i][2]] = false ;
        }
        return r ;
    }
};
