class Solution {
public:
    bool was[510][510];
    int n,m;
    long long ans;
    vector<pair<int,int>> used;

    void update(){
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                was[i][j]=0;
            }
        }
        for(int f=0;f<used.size();f++){
            for(int i=0;i<n;i++){
                for(int j=0;j<m;j++){
                    if(used[f].first==i || used[f].second==j){
                        was[i][j]=1;
                    }
                }
            }
        }
    }

    void dp(vector<vector<int>>& x, int depth, long long sum){
        if(depth==0){
            ans=max(ans,sum);
            return;
        }
        vector<pair<long long,pair<int,int>>> v;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(was[i][j])continue;
                v.push_back({-x[i][j],{i,j}});
            }
        }
        sort(v.begin(),v.end());
        for(int i=0;i<min(10,(int)v.size());i++){
            used.push_back({v[i].second.first,v[i].second.second});
            update();
            dp(x,depth-1,sum-v[i].first);
            used.pop_back();
            update();
        }
    }

    long long maximumValueSum(vector<vector<int>>& x) {
        ans=-((long long)1e18);
        n=x.size();
        m=x[0].size();
        dp(x,3,0);
        return ans;
    }
};
