class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long maxSum = LLONG_MIN;

        // 计算棋盘的全局最大值
        int globalMax = INT_MIN;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                globalMax = max(globalMax, board[i][j]);
            }
        }

        // 枚举第一辆车的位置
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                long long currentSum1 = board[i][j];

                // 剪枝：如果当前和加上2倍全局最大值都不超过maxSum，则跳过
                if (currentSum1 + 2 * globalMax <= maxSum) continue;

                // 枚举第二辆车的位置
                for (int k = i + 1; k < m; ++k) {  // k从i+1开始
                    for (int l = 0; l < n; ++l) {
                        if (l == j) continue;  // 第二辆车不能和第一辆车在同一列
                        long long currentSum2 = currentSum1 + board[k][l];

                        // 剪枝：如果当前和加上全局最大值都不超过maxSum，则跳过
                        if (currentSum2 + globalMax <= maxSum) continue;

                        // 枚举第三辆车的位置
                        for (int p = k + 1; p < m; ++p) {  // p从k+1开始
                            for (int q = 0; q < n; ++q) {
                                if (q == j || q == l) continue;  // 第三辆车不能和前两辆车在同一列
                                long long currentSum3 = currentSum2 + board[p][q];
                                maxSum = max(maxSum, currentSum3);
                            }
                        }
                    }
                }
            }
        }
        return maxSum;
    }
};
