class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) 
    {
        set<pair<int,pair<int,int>>,greater<pair<int,pair<int,int>>>> st;
        vector<pair<int,pair<int,int>>> v;
        int n = board.size();
        int m = board[0].size();
        for(int i = 0; i < n; ++ i)
        {
            for(int j = 0; j < m; ++ j)
            {
                st.insert({board[i][j],{i,j}});
                v.push_back({board[i][j],{i,j}});
            }
        }

        long long finalAns = -1e18;

        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());

        for(int i = 0; i < min(n*m,2*(n+m)); ++i)
        {
            for(int j = i + 1; j < min(n*m,2*(n+m)); ++ j)
            {
                for(int k = j + 1; k < min(n*m,2*(n+m)); ++ k)
                {
                    if((v[i].second.first != v[j].second.first) && (v[j].second.first != v[k].second.first) && (v[k].second.first != v[i].second.first) && (v[i].second.second != v[j].second.second) && (v[j].second.second != v[k].second.second) && (v[k].second.second != v[i].second.second))
                        finalAns = max(finalAns, (long long)((long long)v[i].first + (long long)v[j].first + (long long)v[k].first));
                }
            }
        }


        return finalAns;        
    }
};
