class Solution {
    public long maximumValueSum(int[][] board) {
        final long[][] top3 = new long[board.length][];
        for (int rowIndex = 0; rowIndex < board.length; ++rowIndex) {
            top3[rowIndex] = new long[] { Long.MIN_VALUE, Long.MIN_VALUE, Long.MIN_VALUE, Long.MIN_VALUE, Long.MIN_VALUE, Long.MIN_VALUE };
            final int[] row = board[rowIndex];
            for (int colIndex = 0; colIndex < row.length; ++colIndex) {
                if (row[colIndex] >= top3[rowIndex][1]) {
                    top3[rowIndex][5] = top3[rowIndex][3];
                    top3[rowIndex][4] = top3[rowIndex][2];
                    top3[rowIndex][3] = top3[rowIndex][1];
                    top3[rowIndex][2] = top3[rowIndex][0];
                    top3[rowIndex][1] = row[colIndex];
                    top3[rowIndex][0] = colIndex;
                } else if (row[colIndex] >= top3[rowIndex][3]) {
                    top3[rowIndex][5] = top3[rowIndex][3];
                    top3[rowIndex][4] = top3[rowIndex][2];
                    top3[rowIndex][3] = row[colIndex];
                    top3[rowIndex][2] = colIndex;
                } else if (row[colIndex] >= top3[rowIndex][5]) {
                    top3[rowIndex][5] = row[colIndex];
                    top3[rowIndex][4] = colIndex;
                }
            }
        }

        //System.out.println(Arrays.deepToString(top3));

        long result = Long.MIN_VALUE;
        for (int first = 0; first < board.length; ++first) {
            for (int second = first + 1; second < board.length; ++second) {
                for (int third = second + 1; third < board.length; ++third) {
                    result = Long.max(result, calculateValue(top3, first, second, third));
                }
            }
        }
        return result;
    }

    private long calculateValue(final long[][] top3, int first, int second, int third) {
        long result = Long.MIN_VALUE;
        for (int index1 = 0; index1 < top3[first].length; index1 += 2) {
            for (int index2 = 0; index2 < top3[second].length; index2 += 2) {
                if (top3[first][index1] == top3[second][index2]) {
                    continue;
                }

                for (int index3 = 0; index3 < top3[third].length; index3 += 2) {
                    if (top3[first][index1] == top3[third][index3] || top3[second][index2] == top3[third][index3]) {
                        continue;
                    }

                    //System.out.println(first + "->" + top3[first][index1] + ", " + second + "->" + top3[second][index2]  + ", " + third + "->" + top3[second][index3]);

                    result = Long.max(result, top3[first][index1 + 1] + top3[second][index2 + 1] + top3[third][index3 + 1]);
                }
            }
        }

        return result;
    }
}
