class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n, m = len(board), len(board[0])
        heap = []

        def findSpots(leftover, rows, cols, limit):
            best = float('-inf')
            for i in range(limit + 1, len(heap)):
                score, x, y = heap[i]
                if x not in rows and y not in cols:
                    if leftover > 1:
                        r = set(rows)
                        r.add(x)
                        c = set(cols)
                        c.add(y)
                        tmp = score + findSpots(leftover - 1, r, c, i)
                        if tmp < best:
                            return best
                        else:
                            best = tmp
                    else:
                        return score

            return best


        best = float('-inf')
        for i in range(n):
            for j in range(m):
                heap.append((board[i][j], i, j))

        heap.sort(key=lambda x: (-x[0]))

        for i in range(len(heap)):
            best = max(best, heap[i][0] + findSpots(2, {heap[i][1]}, {heap[i][2]}, i))

        return best
