class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int num_row = board.size();
        int num_col = board[0].size();
        priority_queue<vector<int>, vector<vector<int>>, less<vector<int>>> pq;
        vector<vector<int>> temp;
        for (int row = 0; row < num_row; row++)
            for (int col = 0; col < num_col; col++)
                pq.push({board[row][col], row, col});
        long long int ans = LLONG_MIN;
        for (int index_1 = 0; index_1 < num_row * num_col; index_1++)
            for (int index_2 = index_1 + 1; index_2 < num_row * num_col; index_2++)
            {
                int row_1 = index_1 / num_col;
                int col_1 = index_1 % num_col;
                int row_2 = index_2 / num_col;
                int col_2 = index_2 % num_col;
                if (row_1 == row_2 || col_1 == col_2 || (long long int)board[row_1][col_1] + (long long int)board[row_2][col_2] + (long long int)((pq.empty())? 0 : pq.top()[0]) <= ans)
                    continue;
                while (!pq.empty() && (pq.top()[1] == row_1 || pq.top()[1] == row_2 || pq.top()[2] == col_1 || pq.top()[2] == col_2))
                {
                    temp.push_back(pq.top());
                    pq.pop();
                }
                if (!pq.empty())
                    ans = max(ans, (long long int)board[row_1][col_1] + (long long int)board[row_2][col_2] + (long long int)board[pq.top()[1]][pq.top()[2]]);
                while (!temp.empty())
                {
                    if (temp.back()[1] * num_col + temp.back()[2] > index_1)
                        pq.push(temp.back());
                    temp.pop_back();
                }
            }

        return ans;
    }
};
