typedef struct Node {
    int val;
    int i;
    int j;
    Node(int v, int i = -1, int j = -1) {
        this->val = v;
        this->i = i;
        this->j = j;
    }
}node;

bool cmp(const node& n1, const node& n2) {
    return n1.val > n2.val;
}

class Solution {
public:
    int g=304;
    long long maximumValueSum(const vector<vector<int>>& board) {
        long long ans = 3*(-1e9 - 7);
        vector<node> hep(g, Node(-1e9 - 7));
        make_heap(hep.begin(), hep.end(), cmp);
        for (int i = 0; i < board.size(); ++i) {
            for (int j = 0; j < board[i].size(); ++j) {
                auto& p = hep[0];
                if (p.val <= board[i][j]) {
                    node q{ board[i][j],i,j };
                    pop_heap(hep.begin(), hep.end(), cmp);
                    hep.pop_back();
                    hep.push_back(q);
                    push_heap(hep.begin(), hep.end(), cmp);
                }
            }
        }
        sort_heap(hep.begin(), hep.end(), cmp);
        for (int i = 0; i < g; ++i) {
            const auto& u = hep[i];
            if(u.i==-1){
                continue;
            }
            for (int j = i + 1; j < g; ++j) {
                const auto& v = hep[j];
                if (u.i == v.i || u.j == v.j) {
                    continue;
                }
                if(v.i==-1){
                    continue;
                }
                for (int k = j + 1; k < g; ++k) {
                    const auto& w = hep[k];
                    if(w.i==-1){
                        continue;
                    }
                    if (u.i == w.i || u.j == w.j || v.i == w.i || v.j == w.j) {
                        continue;
                    }
                    else {
                        ans = max(ans, 1LL*u.val + 1LL*v.val + 1LL*w.val);
                    }
                }
            }
        }
        return ans;

    }
};
