class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        // priority_queue<pair<int, int>> p;

        vector<vector<int>> d (n, vector<int>(3));

        for (int i=0;i<n;i++) {
            priority_queue<pair<int, int>> pq;
            for (int j=0;j<m;j++) {
                pq.push({a[i][j], j});
            }

            d[i][0] = pq.top().second;
            pq.pop();
            d[i][1] = pq.top().second;
            pq.pop();
            d[i][2] = pq.top().second;
            pq.pop();
        }

        long long ans=LLONG_MIN;

        for (int i=0;i<=n-3;i++) {
            for (int j=i+1;j<=n-2;j++) {
                for (int k=j+1;k<=n-1;k++) {

                    for (int ii=0;ii<3;ii++) {
                        for (int jj=0;jj<3;jj++) {
                            for (int kk=0;kk<3;kk++) {
                                if (d[i][ii]!=d[j][jj] and d[j][jj]!=d[k][kk] and d[k][kk]!=d[i][ii]) {
                                    long long aa = a[i][d[i][ii]];
                                    aa +=a[j][d[j][jj]];
                                    aa += a[k][d[k][kk]];
                                    ans = max(ans, aa);
                                }
                            }
                        }
                    }


                }
            }
        }

        // long long ans=p.top().first;

        // int i=0;
        // int j=p.top().second;


        // while (p.top().second==j) p.pop();
        // ans += p.top().first;
        // j=p.top().second;
        // while (p.top().second==j) p.pop();
        // ans += p.top().first;
        // j=p.top().second;

        return ans;
    }
};
