class Solution {
public:
#define sc second
#define fs first
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();

        vector<vector<pair<int,int>>>v(n,vector<pair<int,int>>(m));
        for(int i=0;i<n;i++){
              for(int j=0;j<m;j++)
              v[i][j]={board[i][j],j};
        }
    long long ans=-1e15;
        for(int i=0;i<n;i++){

                sort(v[i].rbegin(),v[i].rend());
        //    for(int j=0;j<min(m,4);j++){
        //     cout<<v[i][j].fs<<' '<<v[i][j].sc<<' ';

        //    }
        //    cout<<endl;
        }

        for(int i=0;i<n;i++){
            for(int j=0;j<min(m,4);j++){
                for(int a=i+1;a<n;a++){
                    for(int b=0;b<min(m,4);b++){
                    if(v[i][j].sc!=v[a][b].sc)   { 
                        for(int c=a+1;c<n;c++){
                            for(int d=0;d<min(m,4);d++){
                               if(v[c][d].sc!=v[i][j].sc&&v[c][d].sc!=v[a][b].sc){
                                long long ta=v[i][j].fs;
                                ta+=v[a][b].fs;
                                ta+=v[c][d].fs;
                                ans=max(ans,ta);
                               }

                            }
                        }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
