class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<pair<int,int>>> top(m);
        for (int i = 0;i<m;i++) {
            int first = INT_MIN, second = INT_MIN, third =INT_MIN;
            int firstInd = -1, secondInd = -1, thirdInd = -1;
            for (int j=0;j<n;j++) {
                int value = board[i][j];
                if (value > first) {
                    third = second; 
                    thirdInd = secondInd;
                    second = first;
                    secondInd = firstInd;
                    first = value;
                    firstInd = j;
                } 
                else if (value > second) {
                    third = second; 
                    thirdInd = secondInd;
                    second = value; 
                    secondInd = j;
                } 
                else if (value > third) {
                    third = value;
                    thirdInd = j;
                }
            }
            top[i] = {{first,firstInd},{second,secondInd},{third,thirdInd}};
        }
       long long  ans = LLONG_MIN;
        for (int r1=0;r1<m;r1++) {
            for (int r2=r1+1;r2<m;++r2) {
                for (int r3=r2 + 1; r3 < m; ++r3) {
                    for (auto& p1:top[r1]) {
                        for (auto& p2:top[r2]) {
                            for (auto& p3:top[r3]) {
                            if (p1.second != p2.second && p1.second != p3.second && p2.second != p3.second) {
                                long long currentSum = static_cast<long long>(p1.first) + static_cast<long long>(p2.first) +   static_cast<long long>(p3.first);
                                ans = max(ans, currentSum);
                            }
                        }
                    }
                }
            }
        }
    }
        return ans;

    }
};
