class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long n=board.size();
        long long m=board[0].size();
        vector<vector<pair<long long, pair<long long, long long>>>> topValues(n);
        for (int i = 0; i < n; ++i) {
            vector<pair<long long, pair<long long, long long>>> rowValues;
            for (int j = 0; j < m; ++j) {
                rowValues.push_back({board[i][j], {i, j}});
            }
            sort(rowValues.begin(), rowValues.end(), [](const pair<long long, pair<long long, long long>>& a, const pair<long long, pair<long long, long long>>& b) {
                return a.first > b.first; 
            });

            long long p = min(8LL, m);
            for (int j = 0; j < p; ++j) {
                topValues[i].push_back(rowValues[j]);
            }
        }


         long long maxSum = LLONG_MIN;

        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                for (int k = j + 1; k < n; ++k) {
                    for (const auto& a : topValues[i]) {
                        for (const auto& b : topValues[j]) {
                            for (const auto& c : topValues[k]) {
                                if (a.second.second != b.second.second && a.second.second != c.second.second && b.second.second != c.second.second) {
                                    long long currentSum = a.first + b.first + c.first;
                                    maxSum = max(maxSum, currentSum);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
