#define ll long long

class Solution {
public:
    ll dp[4][103][103][103];
    vector<vector<vector<ll>>> v;

    ll func(ll rookNo,ll row,ll colBl1,ll colBl2,vector<vector<int>> &board){
        if(row==board.size()){
            if(rookNo==3) return 0;
            return -1e15;
        }

        if(dp[rookNo][row][colBl1][colBl2]!=-1) return dp[rookNo][row][colBl1][colBl2];

        ll ans=-1e15;

        ans=max(ans,func(rookNo,row+1,colBl1,colBl2,board));

        for(int ind=0;ind<3;ind++){
            ll i=v[row][ind][1];

            if(i!=colBl1&&i!=colBl2&&rookNo<3){
                if(colBl1==101) ans=max(ans,(ll)board[row][i]+func(rookNo+1,row+1,i,colBl2,board));
                else ans=max(ans,(ll)board[row][i]+func(rookNo+1,row+1,colBl1,i,board));
            }
        }

        // for(ll i=0;i<board[0].size();i++){
        //     ans=max(ans,func(rookNo,row+1,colBl1,colBl2,board));

        //     if(i!=colBl1&&i!=colBl2&&rookNo<3){
        //         if(colBl1==101) ans=max(ans,(ll)board[row][i]+func(rookNo+1,row+1,i,colBl2,board));
        //         else ans=max(ans,(ll)board[row][i]+func(rookNo+1,row+1,colBl1,i,board));
        //     }
        // }

        return dp[rookNo][row][colBl1][colBl2]=ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        memset(dp,-1,sizeof(dp));
        ll row=board.size()+3;
        v.resize(row);

        for(ll i=0;i<board.size();i++){
            for(ll j=0;j<board[0].size();j++){
                v[i].push_back({board[i][j],j});
            }

            sort(v[i].rbegin(),v[i].rend());
        }

        return func(0,0,101,101,board);
    }
};
