class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        int len=n*m;
        vector<vector<long long>> arr(len,vector<long long>(3,0));
        int t=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                arr[t][0]=board[i][j];
                arr[t][1]=i;
                arr[t][2]=j;
                t++;
            }
        }
        sort(arr.begin(),arr.end(),[&](vector<long long>&a,vector<long long>&b){
            return a[0]>b[0];
        });
        long long ans=-3000000001;
        for(int i=0;i<len;i++){
            if(ans>=3*arr[i][0]){
                 break;
            }
            else{
                for(int j=i+1;j<len;j++){
                    if(ans>=(arr[i][0]+2*arr[j][0])){
                         break;
                    }
                    if(arr[i][1]==arr[j][1]||arr[i][2]==arr[j][2]){
                        continue;
                    }
                    else{
                        for(int k=j+1;k<len;k++){
                            if(ans>=(arr[i][0]+arr[j][0]+arr[k][0])){
                                break;
                            }
                            if(arr[i][1]==arr[k][1]||arr[i][2]==arr[k][2]||arr[j][1]==arr[k][1]||arr[j][2]==arr[k][2]){
                                continue;
                            }
                            else{
                                long long tmp=arr[i][0]+arr[j][0]+arr[k][0];
                                ans=max(tmp,ans);
                                break;
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
