from typing import List
import heapq


class Solution:
    def maximumValueSum(self, a: List[List[int]]) -> int:
        n, m = len(a), len(a[0])
        inf = float("inf")

        def solve(r):
            c1, c2, c3 = [-inf] * m, [-inf] * m, [-inf] * m
            for j in range(m):
                c1[j] = max(a[i][j] for i in range(r))
                c2[j] = a[r][j]
                c3[j] = max(a[i][j] for i in range(r + 1, n))
            ans = -inf
            c3_sorted = sorted(c3)
            for i in range(m):
                for j in range(m):
                    if i == j:
                        continue
                    v12 = c1[i] + c2[j]
                    mi, ma = min(c3[i], c3[j]), max(c3[i], c3[j])
                    x = -inf
                    if ma == c3_sorted[-1]:
                        if mi == c3_sorted[-2]:
                            x = c3_sorted[-3]
                        else:
                            x = c3_sorted[-2]
                    else:
                        x = c3_sorted[-1]

                    ans = max(ans, v12 + x)
            return ans

        return max([solve(i) for i in range(1, n - 1)])
