class point{
    int val;
    int col;
    point(int v, int c){
        val=v;
        col=c;
    }
}

class Solution {
    public long maximumValueSum(int[][] board) {

        long ans=Long.MIN_VALUE;
        int m= board.length;
        int n=board[0].length;
        point[][]arr= new point[m][n];
        for(int  i=0;i<m;i++){
            for(int j=0;j<n;j++){
                arr[i][j]= new point(board[i][j],j);
            }
        }

        for(point[]a: arr){
           Arrays.sort(a, new Comparator<point>() {
            @Override
            public int compare(point p1, point p2) {
                return Integer.compare(p2.val, p1.val); // Sort in descending order based on x value
            }
        });
        }



        //now we need to calculate best three
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int k=j+1;k<m;k++){
                    //now suppose our queens are placed in these three rows what is best possible combination for them?

                    for(int ii=0;ii<3;ii++){
                        for(int jj=0;jj<3;jj++){
                            for(int kk=0;kk<3;kk++){

                                if(arr[i][ii].col==arr[j][jj].col  || arr[k][kk].col==arr[j][jj].col || arr[i][ii].col==arr[k][kk].col){
                                    continue;
                                }
                                long temp= ((long)arr[i][ii].val)+((long)arr[j][jj].val)+((long)arr[k][kk].val);
                                    ans= Math.max(ans,temp);
                            }
                        }
                    }
                }
            }
        }


        // for(int i=0;i<m;i++){
        //     for(int j=0;j<n;j++){
        //         System.out.print("("+arr[i][j].val+", "+arr[i][j].col +")");
        //     }
        //     System.out.println();
        // }

        return ans;

    }
}
