class Solution {
public:
#define pii pair<long long, long long>
#define fi first
#define se second
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        vector<vector<pii>> mx(n, vector<pii>(3, pii(-2e9, -1)));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                for (int x = 0; x < 3; ++x)
                    if (a[i][j] >= mx[i][x].fi) {
                        for (int y = 2; y > x; --y)
                            mx[i][y] = mx[i][y - 1];
                        mx[i][x] = pii(a[i][j], j);
                        break;
                    }
            }
        }

        long long ans = -1e18;
        for (int r1 = 0; r1 < n; ++r1)
        for (int r2 = r1 + 1; r2 < n; ++r2)
        for (int r3 = r2 + 1; r3 < n; ++r3) {
            if (mx[r1][0].fi + mx[r2][0].fi + mx[r3][0].fi <= ans)
                continue;
            for (int i = 0; i < 3; ++i)
            for (int j = 0; j < 3; ++j)
            for (int k = 0; k < 3; ++k)
                if (mx[r1][i].se != mx[r2][j].se &&
                    mx[r2][j].se != mx[r3][k].se &&
                    mx[r3][k].se != mx[r1][i].se)
                    ans = max(ans, mx[r1][i].fi + mx[r2][j].fi + mx[r3][k].fi);
        }

        return ans;
    }
};
