class Solution(object):
    def maximumValueSum(self, board):
        """
        :type board: List[List[int]]
        :rtype: int
        """
        m = len(board)
        n = len(board[0])
        con = [() for _ in range(m)]
        for r in range(m):
            e = []
            for c in range(n):
                e.append([board[r][c],c])
            e.sort()
            con[r] = (e[-1], e[-2], e[-3])
        ans = board[0][0] + board[1][1] + board[2][2]
        for r1 in range(m):
            for r2 in range(r1):
                for r3 in range(r2):
                    rs = [r1,r2,r3]
                    for perm in [[0,1,2], [0,2,1], [1,0,2], [1,2,0], [2,0,1], [2,1,0]]:
                        visited = [con[rs[perm[0]]][0][1]]
                        k = con[rs[perm[0]]][0][0]
                        if(con[rs[perm[1]]][0][1] == con[rs[perm[0]]][0][1]):
                            k += con[rs[perm[1]]][1][0]
                            visited.append(con[rs[perm[1]]][1][1])
                        else:
                            k += con[rs[perm[1]]][0][0]
                            visited.append(con[rs[perm[1]]][0][1])
                        if(con[rs[perm[2]]][0][1] in visited):
                            if(con[rs[perm[2]]][1][1] in visited):
                                k += con[rs[perm[2]]][2][0]
                            else:
                                k += con[rs[perm[2]]][1][0]
                        else:
                            k += con[rs[perm[2]]][0][0]
                        ans = max(ans,k)
        return(ans)
