class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        size_t m = board.size();
        size_t n = board[0].size();
        vector<vector<pair<int, int>>> vec(m);
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                vec[i].push_back({board[i][j], j});
            }
            sort(vec[i].begin(), vec[i].end(), greater());
        }
        long long ans = LONG_LONG_MIN;
        for (int i = 0; i < m; ++i) {
            for (int j = i + 1; j < m; ++j) {
                for (int k = j + 1; k < m; ++k) {
                    for (int a = 0; a < 3; ++a) {
                        for (int b = 0; b < 3; ++b) {
                            for (int c = 0; c < 3; ++c) {
                                if (vec[i][a].second == vec[j][b].second) {
                                    continue;
                                }
                                if (vec[i][a].second == vec[k][c].second) {
                                    continue;
                                }
                                if (vec[j][b].second == vec[k][c].second) {
                                    continue;
                                }
                                ans = max(ans, 0LL + vec[i][a].first + vec[j][b].first + vec[k][c].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
