class Solution {
public:
    long long maximumValueSum(vector<vector<int>>&a) {
        int n = a.size(),m = a[0].size();
        vector<vector<pair<long long,int>>> p(n);
        for(int i=0;i<n;i++){
            vector<pair<long long,int>> c;
            for(int j=0;j<m;j++){
                c.push_back({a[i][j],j});
            }

            sort(c.rbegin(),c.rend());
            p[i].push_back(c[0]);
            p[i].push_back(c[1]);
            p[i].push_back(c[2]);
        }

        long long maxi = LONG_MIN;
        for(int i=0;i<p.size();i++){
            for(int j=i+1;j<p.size();j++){
                for(int k=j+1;k<p.size();k++){
                    for(int ii=0;ii<3;ii++){
                        for(int jj=0;jj<3;jj++){
                            for(int kk=0;kk<3;kk++){
                                if(p[i][ii].second != p[j][jj].second && p[j][jj].second!= p[k][kk].second &&
                                p[k][kk].second!=p[i][ii].second){
                                    long long chk = p[i][ii].first+p[k][kk].first+p[j][jj].first;
                                    maxi = max(maxi,chk);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }
};
