class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        l = []
        n, m = len(board), len(board[0])

        for i in range(n):
            for j in range(m):
                l.append((board[i][j], i, j))

        l.sort(key=lambda x: (-x[0], x[1], x[2]))

        # 2 rocks
        rock2 = []
        nm = len(l)
        for i in range(nm):
            val, x, y = l[i]
            c = 0
            for j in range(i+1, nm):
                tval, tx, ty = l[j]
                if tx != x and ty != y:
                    rock2.append((val+tval, x, y, tx, ty))
                    c+=1
                    if c==4:
                        break
        rock3 = []
        r = len(rock2)
        rock2.sort(key=lambda x: -x[0])
        for i in range(r):
            val, x, y , tx, ty = rock2[i]
            for j in range(nm):
                tval, xx, yy = l[j]
                if xx != x and xx!=tx and yy!=y and yy!=ty:
                    rock3.append(val+tval)
                    break    

        return max(rock3)
