class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& ar) {
        int n = ar.size();
        int m = ar[0].size();
        vector<vector<pair<long long, int>>> p(n);
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                p[i].push_back({(long long)ar[i][j], (long long)j});
            }
            sort(p[i].begin(), p[i].end());
            reverse(p[i].begin(), p[i].end());
        }
        long long ans = -3000000000;
        for(int i = 0; i < n; i++) {
            for(int j = i+1; j < n; j++) {
                for(int k = j+1; k < n; k++) {
                    for(int a = 0; a < 3; a++) {
                        for(int b = 0; b < 3; b++) {
                            for(int c = 0; c < 3; c++) {
                                 if(p[i][a].second != p[j][b].second && p[i][a].second != p[k][c].second && p[k][c].second != p[j][b].second) {
                                     ans = max(ans, p[i][a].first + p[j][b].first + p[k][c].first);
                                 }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
