class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) 
    {
        long long ans=(1LL*INT_MIN*3);
        int n=board.size(),m=board[0].size();
        vector<vector<pair<long long ,int>>> mx(n); 
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(mx[i].size()==3)
                {
                    sort(mx[i].begin(),mx[i].end());
                    if(mx[i][0].first<board[i][j])
                    {
                        mx[i][0].first=(long long)board[i][j];
                        mx[i][0].second=j;
                    }
                    sort(mx[i].begin(),mx[i].end());
                }
                else
                {
                    mx[i].push_back({board[i][j],j});
                }
            }
        }
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(i==j)continue;
                for(int k=0;k<n;k++)
                {
                    if(k==j ||k==i)continue;
                    for(int ii=0;ii<3;ii++)
                    {
                        for(int jj=0;jj<3;jj++)
                        {
                            if(mx[i][ii].second==mx[j][jj].second)continue;
                            for(int kk=0;kk<3;kk++)
                            {
                                if(mx[i][ii].second==mx[k][kk].second ||mx[k][kk].second==mx[j][jj].second)continue;
                                ans=max(ans,mx[i][ii].first+mx[k][kk].first+mx[j][jj].first);
                            }
                        }
                    }
                }

            }
        }
        return ans;
    }
};
