class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        // 保存每一行的最大值、次大值和第三大值及其列号
        vector<vector<pair<int, int>>> top3(m, vector<pair<int, int>>(3, {-1, -1}));

        // 计算每行的前3大值以及它们的列号
        for (int i = 0; i < m; ++i) {
            vector<pair<int, int>> values;
            for (int j = 0; j < n; ++j) {
                values.push_back({board[i][j], j});
            }
            sort(values.rbegin(), values.rend());
            for (int k = 0; k < 3 && k < n; ++k) {
                top3[i][k] = values[k];  // 存储前三大值和它们的列号
            }
        }

        // 穷举从三行中选择一个数
        long long maxSum = LLONG_MIN;
        for (int i = 0; i < m; ++i) {
            for (int j = i + 1; j < m; ++j) {
                for (int k = j + 1; k < m; ++k) {
                    // 尝试从三个不同的列中选择值
                    for (int a = 0; a < 3; ++a) {
                        for (int b = 0; b < 3; ++b) {
                            for (int c = 0; c < 3; ++c) {
                                // 确保列号不同
                                if (top3[i][a].second != top3[j][b].second && 
                                    top3[i][a].second != top3[k][c].second &&
                                    top3[j][b].second != top3[k][c].second) {
                                    // 计算当前的价值之和
                                    long long sum = (long long)top3[i][a].first + 
                                                    top3[j][b].first + 
                                                    top3[k][c].first;
                                    maxSum = max(maxSum, sum);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
