class Solution {
    class Node{
        int j;
        int x;
        Node(int j, int x){
            this.j = j;
            this.x = x;
        }
    }
    public long maximumValueSum(int[][] board) {
        int m = board.length, n = board[0].length;
        int inf = 1000000000 + 1;
        Node[][] node = new Node[m][3];
        for(int i = 0; i < m; i++)
            for(int j = 0; j < 3; j++)
                node[i][j] = new Node(-1, -inf);
        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                if(board[i][j] > node[i][0].x){
                    node[i][2] = node[i][1];
                    node[i][1] = node[i][0];
                    node[i][0] = new Node(j, board[i][j]);
                }
                else if(board[i][j] > node[i][1].x){
                    node[i][2] = node[i][1];
                    node[i][1] = new Node(j, board[i][j]);
                }
                else if(board[i][j] > node[i][2].x){
                    node[i][2] = new Node(j, board[i][j]);
                }
            }
        }

        long ans = -4L * (long)inf;

        for(int i = 0; i < m; i++)
        for(int j = i + 1; j < m; j++)
        for(int k = j + 1; k < m; k++)
        for(int x = 0; x < 3; x++)
        for(int y = 0; y < 3; y++)
        for(int z = 0; z < 3; z++)
            if(node[i][x].j != node[j][y].j 
            && node[j][y].j != node[k][z].j 
            && node[k][z].j != node[i][x].j){
                ans = Math.max(ans, (long)node[i][x].x + (long)node[j][y].x + (long)node[k][z].x);
            }

        return ans;
    }
}
