class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long ans = 0;

        // 记录每行最大的三个数及其列索引
        vector<vector<pair<int, int>>> row_max(m);  // pair(值, 列索引)

        for (int i = 0; i < m; ++i) {
            vector<pair<int, int>> row;
            for (int j = 0; j < n; ++j) {
                row.emplace_back(board[i][j], j);
            }
            // 每行取出最大的三个数
            sort(row.rbegin(), row.rend());  // 从大到小排序
            for (int k = 0; k < min(3, n); ++k) {
                row_max[i].push_back(row[k]);
            }
        }

        long long max_value = LLONG_MIN;  // 用于存储结果

        // 遍历所有行的组合
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    // 对每行尝试不同列的组合
                    for (const auto& c1 : row_max[r1]) {
                        for (const auto& c2 : row_max[r2]) {
                            if (c1.second == c2.second) continue;  // 确保列不同
                            for (const auto& c3 : row_max[r3]) {
                                if (c1.second == c3.second || c2.second == c3.second) continue;  // 确保列不同
                                // 计算当前组合的值
                                long long current_value = (long long)c1.first + c2.first + c3.first;
                                max_value = max(max_value, current_value);
                            }
                        }
                    }
                }
            }
        }

        return max_value;
    }
};
