class Solution
{
public:
    const int fincheck = 10;
    struct node
    {
        int x;
        int y;
        int val;
    };

    struct compare
    {
        bool operator()(node const &A, node const &B)
        {
            return A.val < B.val;
        }
    };
    long long maximumValueSum(vector<vector<int>> &a)
    {

        int m = a.size();
        int n = a[0].size();

        long long ans = -1e13;
        priority_queue<pair<int, pair<int, int>>, vector<pair<int, pair<int, int>>>> val;
        priority_queue<node, vector<node>, compare> maxheap,maxheap2,maxheap3;
        node temp,curnode;
        int i, j, k;
        for (i = 0; i < m; i++)
        {
            for (j = 0; j < n; j++)
            {
                temp.x = i;
                temp.y = j;
                temp.val = a[i][j];
                maxheap.push(temp);
            }
           // outo(maxheap.top().val);
        }

        int checked=0;
        int visr[105];
        int visc[105];

        for(i=0;i<105;i++)
        {
            visr[i] = 0;
            visc[i] = 0;
        }
        while(!maxheap.empty() && ++checked < fincheck)
        {
            curnode = maxheap.top();
            maxheap.pop();

            visr[curnode.x] = 1;
            visc[curnode.y] = 1;

            for(i=0;i<m;i++)
            {
                if(visr[i])
                continue;
                for(j=0;j<n;j++)
                {
                    if(visc[j])
                    continue;
                    temp.x = i;
                    temp.y = j;
                    temp.val = a[i][j];
                    maxheap2.push(temp);
                }
            }

            int checked2 = 0;
            int vr[105];
            int vc[105];
            for(i=0;i<105;i++)
            {
                vr[i] = 0;
                vc[i] = 0;
            }
            while(!maxheap2.empty() && ++checked2 < fincheck)
            {
                node cur2 = maxheap2.top();
                maxheap2.pop();
                vr[cur2.x] = 1;
                vc[cur2.y] = 1;

                for(i=0;i<m;i++)
                {
                    if(vr[i] || visr[i])
                    continue;
                    for(j=0;j<n;j++)
                    {
                        if(vc[j] || visc[j])
                        continue;
                        long long tans = a[i][j];
                        tans += curnode.val;
                        tans += cur2.val;

                        ans = max(ans,tans);
                    }
                }

                vr[cur2.x] = 0;
                vc[cur2.y] = 0;

            }
            visr[curnode.x] = 0;
            visc[curnode.y] = 0;

        }

        return ans;
    }
};
