class Solution {
public:
    long long work(vector<pair<int, int>>& a, vector<pair<int, int>>& b, vector<pair<int, int>>& c) {
        long long val = a[0].first;
        int y1 = a[0].second, y2 = y1;

        //cout << val << ' ';

        if (b[0].second == y1) val += b[1].first, y2 = b[1].second;
        else val += b[0].first, y2 = b[0].second;

        //cout << val << ' ';

        if (c[0].second != y1 && c[0].second != y2) val += c[0].first;
        else if (c[1].second != y1 && c[1].second != y2) val += c[1].first;
        else val += c[2].first;

        //cout << val << '\n';

        return val;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board.back().size();
        vector<vector<pair<int, int>>> a(n, vector<pair<int, int>>(m, {0, 0}));

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) a[i][j] = {board[i][j], j};
            sort(a[i].begin(), a[i].end(), [](pair<int, int>& x, pair<int, int>& y) { return x.first > y.first; });
        }

        long long ans = -1e18;
        for (int x1 = 0; x1 < n; ++x1) for (int x2 = 0; x2 < n; ++x2) if (x2 != x1)
            for (int x3 = 0; x3 < n; ++x3) if (x3 != x2 && x3 != x1) {
                //cout << x1 << ' ' << x2 << ' ' << x3 << ' ' << work(a[x1], a[x2], a[x3]) << '\n';
                ans = max(ans, work(a[x1], a[x2], a[x3]));
            }
        return ans;
    }
};
